require(['jquery'],function($) {
  const hasEditRights = XWiki.hasEdit;
  const actionDoc = new XWiki.Document(XWiki.Model.resolve('Collabora.Code.Main', XWiki.EntityType.DOCUMENT));

  var getButtonTemplate = function(hasPagination) {
    const config = JSON.parse($('#collabora-config').text());
    return hasPagination ? $(config.tableButtonTemplate) : $(config.listButtonTemplate);
  };

  var getExtAcceptedAction = function() {
    const canDoByExt = {};
    ['doc', 'docx', 'xls', 'xlsx', 'ppt', 'pptx', 'csv', 'rtf', 'txt', 'odt', 'ods', 'odp', 'odg'].forEach(function(x) {
      canDoByExt[x] = 'edit';
    });
    ['pdf', 'fb2'].forEach(function(x) {
      canDoByExt[x] = 'view';
    });
    return canDoByExt;
  };

  var getAccessRights = function(fileName, canDoByExt) {
    const fileType = fileName.slice(fileName.lastIndexOf('.') + 1).toLowerCase();
    let accessRights = canDoByExt[fileType];
    if (!accessRights) {
      return false;
    }
    if (!XWiki.hasEdit) {
      accessRights = 'view';
    }

    return accessRights;
  };

  var getCollaboraButton = function(thisButton, fileName, accessRights) {
    // The data translations attributes are used for updating the title from js, since it depends on the access rights.
    // This can be changed once licensing starts depending on a XWiki version >= 13.8 to include
    // XWIKI-18973: Simplify the way JavaScript code loads translation messages.
    const title = thisButton.data(accessRights == 'edit' ? 'editTitle' : 'viewTitle');
    thisButton.attr('title', title);
    thisButton.find('img').attr('alt', title);
    thisButton.removeAttr('data-edit-title');
    thisButton.removeAttr('data-view-title');

    // Set action URL.
    const queryString = $.param({
      'document': XWiki.Document.currentDocumentReference.toString(),
      'filename': fileName,
      'action': accessRights,
      'xpage': 'plain'
    });
    thisButton.attr('href', actionDoc.getURL('get', queryString));
    return thisButton;
  };

  var decorateWithCollaboraButtons = function() {
    // Attachments are displayed using a livetable since 14.6, and using a livedata from 14.8.
    const hasPagination = $("#_attachments table").length > 0;
    const buttonTemplate = getButtonTemplate(hasPagination);
    const canDoByExt = getExtAcceptedAction();

    $("#_attachments span.name").each(function(i, attach) {
      const attachActions = hasPagination ? $(attach).closest('tr').find('.attachmentActions') : $(attach).siblings('.xwikibuttonlinks');
      if ($(attachActions).find('a.collaboraEdit').length) {
        return;
      }

      const fileName = $(attach).find('a').text().trim();
      const accessRights = getAccessRights(fileName, canDoByExt);
      if (!accessRights) {
        return;
      }

      $(attachActions).append(getCollaboraButton(buttonTemplate.clone(), fileName, accessRights));
    });
  };

  $(decorateWithCollaboraButtons);
  $(document).on('xwiki:livedata:afterEntryFetch', function (event) {
    if ($(event.target).attr('id') === 'docAttachments') {
      // We need to setTimeout(0) to allow the live data to display the entries before decorating them with the buttons.
      setTimeout(decorateWithCollaboraButtons, 0);
    }
  });
  $(document).on('xwiki:livetable:docAttachments:loadingComplete', decorateWithCollaboraButtons);
  $(document).on('xwiki:html5upload:fileFinished', decorateWithCollaboraButtons);
  $(document).on('xwiki:docextra:loaded', decorateWithCollaboraButtons);
});
